CREATE TABLE mtorg_db.rainlab_blog_posts
(
    id int(10) unsigned PRIMARY KEY NOT NULL AUTO_INCREMENT,
    user_id int(10) unsigned,
    title varchar(191),
    slug varchar(191) NOT NULL,
    excerpt text,
    content longtext,
    content_html longtext,
    published_at timestamp,
    published tinyint(1) DEFAULT 0 NOT NULL,
    created_at timestamp,
    updated_at timestamp
);
CREATE INDEX rainlab_blog_posts_user_id_index ON mtorg_db.rainlab_blog_posts (user_id);
CREATE INDEX rainlab_blog_posts_slug_index ON mtorg_db.rainlab_blog_posts (slug);
