lid = validateEmail(elForm.elements["mauticform[email]"].value);                     markError(''mauticform_email'', valid);                     if (!valid) formValid = false;                      if (formValid) {                         document.getElementById(formId + ''_return'').value = document.URL;                     }                     return formValid;                 },                  checkMessages: function () {                     var query = {};                     location.search.substr(1).split("&").forEach(function (item) {                         query[item.split("=")[0]] = item.split("=")[1]                     });                     if (typeof query.mauticError !== ''undefined'') {                         var errorContainer = document.getElementById(formId + ''_error'');                         errorContainer.innerHTML = decodeURIComponent(query.mauticError);                     } else if (typeof query.mauticMessage !== ''undefined'') {                         var messageContainer = document.getElementById(formId + ''_message'');                         messageContainer.innerHTML = decodeURIComponent(query.mauticMessage);                     }                 }             };              validator.checkMessages();              return validator;         };      </script>', '2014-09-02 07:34:07', 1, '2014-09-02 07:34:07', '2014-09-02 07:34:07');
