    <span class="hljs-variable">$mauticBaseUrl</span> . <span class="hljs-string">''/api/''</span>
);

<span class="hljs-variable">$lead</span> = <span class="hljs-variable">$leadApi</span><span class="hljs-variable">-&gt;create</span>(<span class="hljs-keyword">array</span>(
