INSERT INTO mtorg_db.rainlab_blog_posts (id, user_id, title, slug, excerpt, content, content_html, published_at, published, created_at, updated_at) VALUES (981, 0, 'An Introduction to Marketing Automation', 'an-introduction-to-marketing-automation', '', '<iframe src="https://www.youtube.com/embed/QT6169rdMdk?rel=0&showinfo=0" width="740" height="416" frameborder="0" allowfullscreen="allowfullscreen"></iframe>

### Introduction to Marketing Automation Slides

