CREATE TABLE mtorg_db.rainlab_forum_topics
(
    id int(10) unsigned PRIMARY KEY NOT NULL AUTO_INCREMENT,
    subject varchar(191),
    slug varchar(191) NOT NULL,
    channel_id int(10) unsigned NOT NULL,
    start_member_id int(11),
    last_post_id int(11),
    last_post_member_id int(11),
    last_post_at datetime,
    is_private tinyint(1) DEFAULT 0 NOT NULL,
    is_sticky tinyint(1) DEFAULT 0 NOT NULL,
    is_locked tinyint(1) DEFAULT 0 NOT NULL,
    count_posts int(11) DEFAULT 0 NOT NULL,
    count_views int(11) DEFAULT 0 NOT NULL,
    created_at timestamp,
    updated_at timestamp,
    embed_code varchar(191)
);
CREATE UNIQUE INDEX rainlab_forum_topics_slug_unique ON mtorg_db.rainlab_forum_topics (slug);
CREATE INDEX rainlab_forum_topics_channel_id_index ON mtorg_db.rainlab_forum_topics (channel_id);
CREATE INDEX rainlab_forum_topics_start_member_id_index ON mtorg_db.rainlab_forum_topics (start_member_id);
CREATE INDEX rainlab_forum_topics_last_post_at_index ON mtorg_db.rainlab_forum_topics (last_post_at);
CREATE INDEX rainlab_forum_topics_is_private_index ON mtorg_db.rainlab_forum_topics (is_private);
CREATE INDEX sticky_post_time ON mtorg_db.rainlab_forum_topics (is_sticky, last_post_at);
CREATE INDEX rainlab_forum_topics_is_locked_index ON mtorg_db.rainlab_forum_topics (is_locked);
CREATE INDEX rainlab_forum_topics_count_posts_index ON mtorg_db.rainlab_forum_topics (count_posts);
CREATE INDEX rainlab_forum_topics_count_views_index ON mtorg_db.rainlab_forum_topics (count_views);
CREATE INDEX rainlab_forum_topics_embed_code_index ON mtorg_db.rainlab_forum_topics (embed_code);
