INSERT INTO mtorg_db.trka_groups_groups (id, name, slug, description, parent, created_at, updated_at, deleted_at, forum_channel_id, homepage_content, homepage_content_html, group_type, group_icon, group_color, transifex_url, notices, sidebar_slots) VALUES (46, 'Mauticians Working Group', 'mauticians-working-group', 'Keeping track of who''s doing what and all the many tens of thousands of Mauticians is a daunting task. We like challenges though. Our Mauticians working group focuses on helping Mauticians get connected and share their expertise.', 0, '2018-06-28 12:42:06', '2018-06-30 13:15:26', null, 104426, '', '', 'working-group', 'fe-user-check', '', null, null, null);
INSERT INTO mtorg_db.trka_groups_groups (id, name, slug, description, parent, created_at, updated_at, deleted_at, forum_channel_id, homepage_content, homepage_content_html, group_type, group_icon, group_color, transifex_url, notices, sidebar_slots) VALUES (47, 'Mauticon Working Group', 'mauticon-working-group', 'Some groups manage lots of little events, our working group focuses all our attention on creating the single, biggest, and best Mautic-focused event of the year. Want to help organize the annual Mauticon? Join us!', 0, '2018-06-28 12:42:35', '2018-06-30 13:15:32', null, 104427, '', '', 'working-group', 'fe-cast', '', null, null, null);
INSERT INTO mtorg_db.trka_groups_groups (id, name, slug, description, parent, created_at, updated_at, deleted_at, forum_channel_id, homepage_content, homepage_content_html, group_type, group_icon, group_color, transifex_url, notices, sidebar_slots) VALUES (48, 'Mauticamps Working Group', 'mauticamps-working-group', 'Can you imagine the fun of getting to work with people from all over the world? Our working group is focused on empowering local Mauticamps in cities across the globe. If you like giving people helpful tools to be successful running events join our group!', 0, '2018-06-28 12:42:52', '2018-06-30 13:15:20', null, 104425, '', '', 'working-group', 'fe-triangle', '', null, null, null);
