INSERT INTO mtorg_db.trka_groups_groups (id, name, slug, description, parent, created_at, updated_at, deleted_at, forum_channel_id, homepage_content, homepage_content_html, group_type, group_icon, group_color, transifex_url, notices, sidebar_slots) VALUES (50, 'Trademark Working Group', 'trademark-working-group', 'Do you love the Mautic brand and name as much as we do? If you''re interested in making sure Mautic looks right everywhere you see it then this is the working group for you.', 0, '2018-06-28 12:44:09', '2018-06-30 13:16:08', null, 104433, '', '', 'working-group', 'fe-heart', '', null, null, null);
INSERT INTO mtorg_db.trka_groups_groups (id, name, slug, description, parent, created_at, updated_at, deleted_at, forum_channel_id, homepage_content, homepage_content_html, group_type, group_icon, group_color, transifex_url, notices, sidebar_slots) VALUES (51, 'Strategy + Program Working Group', 'strategy-program-working-group', 'Every marketing department needs a strategy. Mautic is no different, and possibly even more so since we''re the world''s best marketing automation platform. Our marketing is super critical. If you like strategy, this team is for you.', 0, '2018-06-28 12:44:43', '2018-06-30 13:16:03', null, 104432, '', '', 'working-group', 'fe-map', '', null, null, null);
